    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if(jButton3.getText().equals("+")){
            setSize(430, 550);
            jButton3.setText("-");
        }else{
            setSize(430, 350);
            jButton3.setText("+");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int qtyKids = (int) jSpinner1.getValue();
        int qtyAdults = (int) jSpinner2.getValue();
        float qtyCriancaRefri = 0.5f;
        float qtyAdultoRefri = 1.5f;
        int qtyCriancaSalgado = 5;
        int qtyAdultoSalgado = 15;
        int qtyCriancaDoce = 7;
        int qtyAdultoDoce = 10;
        int valorLitroRefri = 8;
        float valorSalgados = 0.7f;
        float valorDoces = 1.2f;
        
        float totalRefri = ((qtyCriancaRefri * qtyKids) + (qtyAdultoRefri * qtyAdults)) * valorLitroRefri;
        float totalSalgados = ((qtyCriancaSalgado * qtyKids) + (qtyAdultoSalgado * qtyAdults)) * valorSalgados;
        float totalDoces = ((qtyCriancaDoce * qtyKids) + (qtyAdultoDoce * qtyAdults)) * valorDoces;
        float totalgeral = totalRefri + totalSalgados + totalDoces;
        jTextField1.setText(String.format("R$%.2f", totalgeral));
        jTextArea1.setText(String.format("Total Refrigerante: R$%.2f \nTotal Salgados: R$%.2f\nTotal Doces: R$%.2f",totalRefri,totalSalgados,totalDoces));
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

